import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

}
export class Login{
  public email: string;
  public password: string;

  LoginForm = new FormGroup(
    email: new FormControl(Validators.minLength(2)),
    password: new FormControl(Validators.minLength(8))
  )
}
<div class="form-wrapper">
    <div class="section-title">
     <h3>Login Form</h3> <hr/>
    </div>
    <form [formGroup] = "LoginForm" >
        <div class="form-group">
            <label for="email">Email</label>
            <input type="email" formcontrolname="email" class="form-control" id="email" placeholder="Enter your email.."/>
            <small class="">*Email is Required</small>
        </div>
        <div class="form-group">
            <label for="password">Password</label>
            <input type="password" formcontrolname="password" class="form-control" id="password" placeholder="Enter your password.." />

        </div>
            <small>Not Signed up? </small>
            <a routerLink="/signup">SignUp</a>

        <div class="form-group">
            <input type="button" class="btn btn-outline-success" value="LogIn">
        </div>
    </form>
</div>
